import{_ as t}from"./chunks/latexdff-v1.Cnrau3LD.js";import{_ as a,c as i,o as s,a6 as o}from"./chunks/framework.fkSL6LTs.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{"layout":"home","hero":{"name":"TeXRA","text":"Your Intelligent Academic Research Assistant","tagline":"AI assistance to help with your academic research in VS Code","image":{"src":"/logo-1024x1024.svg","alt":"TeXRA Logo"},"actions":[{"theme":"brand","text":"Get Started","link":"/guide/"},{"theme":"alt","text":"Install from Marketplace","link":"https://marketplace.visualstudio.com/items?itemName=texra-ai.texra"},{"theme":"alt","text":"Try it on Web","link":"/launch"},{"theme":"alt","text":"View on GitHub","link":"https://github.com/texra-ai/texra-issues"}]}},"headers":[],"relativePath":"index.md","filePath":"index.md"}'),r={name:"index.md"};function n(l,e,c,d,p,u){return s(),i("div",null,e[0]||(e[0]=[o('<div class="workflow-container"><div class="workflow-steps"><div class="workflow-step"><div class="step-number">1</div><div class="step-content"><h3>Select Content</h3><p>Choose your input files, references, figures, and specify output</p><div class="step-icon">📄</div></div></div><div class="workflow-arrow">→</div><div class="workflow-step"><div class="step-number">2</div><div class="step-content"><h3>Choose Agent/Model</h3><p>Select a specialized agent (correct, polish)</p><div class="step-icon">🤖</div></div></div><div class="workflow-arrow">→</div><div class="workflow-step"><div class="step-number">3</div><div class="step-content"><h3>Execute</h3><p>TeXRA handles the rest with powerful language models</p><div class="step-icon">⚡</div></div></div><div class="workflow-arrow">→</div><div class="workflow-step"><div class="step-number">4</div><div class="step-content"><h3>Review Changes</h3><div class="step-image"><a href="/examples/draft_polish_r1_gemini25p_diff.pdf" target="_blank"><img src="'+t+'" alt="LaTeX Diff visualization" class="workflow-diff-image"><div class="view-pdf">View full PDF example</div></a></div></div></div></div></div><h2 id="why-texra" tabindex="-1">Why TeXRA? <a class="header-anchor" href="#why-texra" aria-label="Permalink to &quot;Why TeXRA?&quot;">​</a></h2><p>Standard LLM interfaces struggle with the nuances of academic work – complex formatting, precise terminology, large documents, and multi-step reasoning. TeXRA overcomes these limitations by integrating AI deeply into your workflow with:</p><ul><li><strong>Agentic Design</strong>: Specialized agents tackle specific tasks like correcting, polishing, drawing, and transforming documents.</li><li><strong>Reflection &amp; Tool Use</strong>: Agents can critique their own work and leverage external tools (like <code>latexdiff</code>, <code>texcount</code>) for enhanced accuracy and observability.</li><li><strong>Context Awareness</strong>: Seamlessly handles multi-file projects and references, giving you control over the context provided to the LLM (up to its limits).</li><li><strong>Transparency &amp; Control</strong>: Uses a structured, customizable agent system – not a black box.</li></ul><h2 id="key-capabilities" tabindex="-1">Key Capabilities <a class="header-anchor" href="#key-capabilities" aria-label="Permalink to &quot;Key Capabilities&quot;">​</a></h2><div class="features-grid"><div class="feature-item"><h3>📝 Smart Editing &amp; Polishing</h3><p>Go beyond simple grammar checks. Improve clarity, flow, and style while preserving technical accuracy. Fix subtle LaTeX errors and ensure consistency.</p><div class="feature-example"><span style="color:var(--vp-c-red);text-decoration:line-through;">The results shows significant increase.</span><br><span style="color:var(--vp-c-brand);">The results show a significant increase.</span></div></div><div class="feature-item"><h3>🎨 TikZ Figure Generation</h3><p>Create complex TikZ diagrams from natural language descriptions or enhance existing figures. TeXRA handles the code generation and compilation.</p><div class="feature-example"><code>&quot;Draw a flowchart for...&quot;</code> → `\\begin{tikzpicture}...` </div></div><div class="feature-item"><h3>🔄 Document Transformation</h3><p>Effortlessly convert papers into slides (`paper2slide`), lecture notes (`paper2note`), posters (`paper2poster`), or even draft cover letters (`paper2cover`).</p><div class="feature-example"><code>Paper Abstract</code> → `Beamer Slides Outline` </div></div><div class="feature-item"><h3>📊 LaTeX-Aware Processing</h3><p>Understands LaTeX structure, math, citations, and environments. Includes integrated `latexdiff` for visualizing changes and `texcount` for analysis.</p><div class="feature-example"><span style="color:var(--vp-c-red);text-decoration:line-through;">E = mc</span><br><span style="color:var(--vp-c-brand);">E = mc^2 \\label{eq:einstein}</span> (via `correct` agent) </div></div><div class="feature-item"><h3>🧩 Multi-File Projects</h3><p>Handle books, theses, or papers split across multiple files. Apply changes consistently or target specific sections.</p><div class="feature-example"><code>[ch1.tex, ch2.tex]</code> → <code>[ch1_polished.tex, ch2_polished.tex]</code></div></div><div class="feature-item"><h3>🖼️ Multimodal Understanding</h3><p>Leverage vision-capable models to analyze images, figures, and PDFs directly within your workflow.</p><div class="feature-example"><code>&lt;figure.png&gt; + &quot;Write caption&quot;</code> → <code>\\caption{...}</code></div></div></div><h2 id="get-started-today" tabindex="-1">Get Started Today <a class="header-anchor" href="#get-started-today" aria-label="Permalink to &quot;Get Started Today&quot;">​</a></h2><p>Installing TeXRA is simple. Follow our <a href="/guide/installation.html">Installation Guide</a> to get set up in minutes.</p><div class="cta-container"><a href="/guide/" class="cta-button">Explore the Documentation</a><a href="/guide/quick-start" class="cta-button cta-button-alt">Quick Start Guide</a></div>',9)]))}const f=a(r,[["render",n]]);export{m as __pageData,f as default};
